import React, {useState, useEffect} from 'react'
import SwiperCore ,{ Autoplay } from 'swiper'
import {Swiper, SwiperSlide} from 'swiper/react'
import 'swiper/swiper-bundle.min.css'
import 'swiper/swiper.min.css'
import tmdbApi, {category, movieType} from '../../api/tmdbAPI'
import apiConfig from '../../api/APIConfig'
import Button from '../Button/Button'
import "./HeroSlide.scss"


const HeroSlide = () => {
  
    const [movies, setMovies] = useState([])
    useEffect(() => {
        const getMovies = async () => {
            const params = {page: 1}
            try {
                const response = await tmdbApi.getMoviesList(movieType.popular, {params});
                setMovies(response.results.slice(1,10))
                console.log(response)
            } catch (error) {
                console.log(error)
            }

        }
        getMovies();
    },[])
    return (
        <div className="hero-slide">
            <Swiper
                modules={[Autoplay]}
                grabCursor={true}
                spaceBetween={0}
                slidesPerView={1}
                autoplay={{delay: 3000}}
            >
                {
                    movies.map((item, i) => (
                        <SwiperSlide key={i}>
                            {({ isActive }) => (
                                <HeroSlideItem item={item}/>
                            )}
                        </SwiperSlide>
                    ))
                }
            </Swiper>
           
        </div>
    );
}

const HeroSlideItem = props => {
    const item = props.item;
    const background = apiConfig.originalImage(item.backdrop_path ? item.backdrop_path : item.poster_path)
    const poster = apiConfig.w500Image(item.poster_path)
    return (
        <div className="hero-slide">
            <div className="movie" style={{backgroundImage: `url(${background})`}}>
            <div className="overlay"></div>
            <div className="movie-info">
            <div className="movie-desc">
                <h1>{item.title}</h1>
                <p>{item.overview}</p>
                <Button >Watch Now</Button>
                <Button btnstyle='btn--outlined'>Watch Trailer</Button>
            </div>
            <div className="movie-poster">
                <img src={poster} alt={item.title} />
            </div>
            </div>
        </div>
        </div>
    )
    
}
export default HeroSlide
